import React, { useState } from "react";


const Navbar = (props) => {

  const[vala, valb]=useState("");
  // const[valc, vald]=useState("");

  // const get = vala.lenght();


  const toUpp = () =>{
    const a = vala;
    valb(a.toLocaleUpperCase());


  }
  const toLow = () =>{
    // const a = vala;
    valb(vala.toLocaleLowerCase());


  }




 const clickHandler = (event) =>{
  valb(event.target.value);

 } 
return(
<>
<nav className="navbar navbar-expand-lg navbar-light bg-light">/
  <div className="container-fluid">
    <a className="navbar-brand" href="/">{props.title}</a>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="/">{props.nav1}</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/">{props.nav2}</a>
        </li>
        </ul>
    </div>
  </div>
</nav>

<div className="container my-3">
<h1>Enter Your Text here</h1>

<div class="mb-3">
  <label for="exampleFormControlTextarea1" class="form-label"></label>
  <textarea class="form-control" id="exampleFormControlTextarea1" rows="8" value={vala} onChange={clickHandler}></textarea>
</div>
<button className="btn btn-primary" onClick={toUpp}>ToUpperCase</button>
<button className="btn btn-primary mx-2" onClick={toLow}>ToLowerCase</button>

<p>Words{}</p>

<h4>Summery</h4>
<p>{vala}</p>
</div>
</>
)

}
export default Navbar;
